class errorDB extends Error {
  constructor(message, type, method, key, value) {
    super(message)
    
    this.name = 'MEGADB [ERROR]'
    this.type = type 
    this.method = method
    this.param1 = key
    this.param2 = value
    this.reference = `<DB>.${method}(${key !== undefined ? (typeof key == 'string' ? '"' + key + '"' : key) : key}${['get', 'obtener', 'existeDB', 'tiene', 'has', 'eliminar', 'delete', 'keys', 'values', 'readJSON', 'saveJSON', 'crearDB'].includes(method) ? ')' : (typeof value == 'string' ? ', "' + value + '")' : ', ' + value + ')')}`
  }
}

module.exports = errorDB