const { saveJSON } = require("../utils/utilidades.js"),
				errorDB = require("../class/errorDB.js");

module.exports = {
	extract: function(clave, valor, split_object, global_object, self, save) {

	  if(!valor && valor != 0) throw new errorDB(`El valor no fue especificado`, 'VALOR INVALIDO', 'extract', clave, valor)

	  var [...args] = clave.split(split_object)
	  var object_data = global_object[self.object_id]

	  for(var key of args) {
			if(!object_data.hasOwnProperty(key)) return Promise.reject(new errorDB(`No se encontro la propiedad ${key}, base de datos: ${self.database_name}`, 'PROPIEDAD NO EXISTENTE', 'extract', clave, valor))
			if(key == args[args.length-1]) {
				if(!Array.isArray(object_data[key])) return Promise.reject(new errorDB(`El valor de la propiedad ${key} no es un array, base de datos: ${self.database_name}`, 'ARRAY NO EXISTENTE', 'extract', clave, valor))
				if(!object_data[key].includes(valor)) return Promise.reject(new errorDB(`El item ${valor} no existe en el array ${key}, base de datos: ${self.database_name}`, 'ITEM NO EXISTENTE', 'extract', clave, valor))
				object_data[key].splice(object_data[key].indexOf(valor), 1)
				if(!save) return Promise.resolve(object_data[key])
				saveJSON(self, global_object[self.object_id])
				return Promise.resolve(object_data[key])
			}
			else {
				object_data = object_data[key]
			}
		}
	}
}

