const { saveJSON } = require("../utils/utilidades.js")

module.exports = {
	purgeall: function (global_object, self, save) {
	  if(Object.keys(global_object[self.object_id]).length <= 0) return true
	  global_object[self.object_id] = {}
	  if(!save) return true
	  saveJSON(self, global_object[self.object_id])
	  return true
	}
}